import React, {useState, useEffect} from 'react'
import TimerButtons from './TimerButtons'
import TimeFormat from './TimeFomat.js'


export default function Timer(props) {
    const {time, time: {start, end, active}, setTime, setTimeEntry} = props;

    const [timeUpdate, setTimeUpdate] = useState(Date.now());



    useEffect(() => {
        const interval = setInterval(() => setTimeUpdate(Date.now()), 1000);
        return () => {
            clearInterval(interval)
        }
    }, [])

    return (
        <div className='timer-container'>
            <div className='timer'>
                {active ? timeFormat(start, Date.now()) : timeFormat(start, end)}
            </div>
            <TimerButtons time={time} setTime={setTime} setTimeEntry={setTimeEntry} timeFormat={timeFormat} />
        </div>
    )
}
