import React, {useState, useEffect} from 'react'
import TimerButtons from './TimerButtons'


export default function Timer(props) {
    const {time, time: {start, end, active}, setTime, setTimeEntry} = props;

    const [timeUpdate, setTimeUpdate] = useState(new Date());

    const timeFormat = (start, end) => {
        const hours = end.getUTCHours() - start.getUTCHours();
        const minutes = end.getUTCMinutes() - start.getUTCMinutes();
        const seconds = end.getUTCSeconds() - start.getUTCSeconds();

        return (
            <span>{hours} : {seconds} : {minutes}</span>
        )
    }

    useEffect(() => {
        const interval = setInterval(() => setTimeUpdate(new Date()), 1000);
        return () => {
            clearInterval()
        }
    }, [])

    return (
        <div className='timer-container'>
            <div className='timer'>
                {active ? timeFormat(start, new Date()) : timeFormat(start, end)}
            </div>
            <TimerButtons time={time} setTime={setTime} setTimeEntry={setTimeEntry} />
        </div>
    )
}
